function gettxt() {
    var q = "অয্যাযো"
    var p = document.querySelectorAll('p')
    var a = []
    var start = new Date().getTime();
    var mm;

    for (i = 0; i < p.length; ++i) {
        // do something
        if (p[i].textContent.includes(q)) {
            //	console.log(i, m)
            a.push(p[i].innerHTML)
            var m = p[i].innerHTML
            var re = new RegExp(q, "gi"); // create a a | b | c regex
            mm = m.replace(re, function replace(match) { // wrap the found strings
                return `<mark id="${i}">${match}</mark>`;
            });
            document.body.insertAdjacentHTML('afterbegin', `<li style="font-size: 12px;"> ${mm}</li`)
            document.querySelectorAll('p')[i].innerHTML = mm
        }
    }
    
    var end = new Date().getTime();
    var time = end - start;
    alert(' Total time: ' + time + 'mili second');
    //	console.log(a)
    console.log(a.length)
}


